namespace Arikaim\Core\Db\Prototypes\Column;

use Arikaim\Core\Db\Interfaces\BlueprintPrototypeInterface;

/**
 * MetaTags column prototype class
*/
class MetaTags implements BlueprintPrototypeInterface
{
    /**
     * Build column
     *
     * @param \Arikaim\Core\Db\TableBlueprint $table
     * @param mixed $options
     * @return void
     */
    public function build($table,...$options)
    {
        $table->string('meta_title')->nullable(true);
        $table->text('meta_description')->nullable(true);
        $table->text('meta_keywords')->nullable(true);                   
    }
}
